import { DirectoryGenerator } from './generators';
import { IGenerator } from './Interfaces';
import { FileType } from './enums';

/**
 * GeneratorFactory, the factory implementation for the set of generators
 * to deliver the correct generator implementation for each type of source code
 */
export class GeneratorFactory {
  private _generatorMap: { [key: string]: IGenerator } = {};

  constructor() {
    this._loadDefaultGenerators();
  }

  /**
   * Used at the constructor to load the directory generator for files and folders
   */
  private _loadDefaultGenerators(): void {
    const dirGenerator = new DirectoryGenerator();
    this.addGenerator(dirGenerator, FileType.File);
    this.addGenerator(dirGenerator, FileType.Folder);
  }

  /**
   * Add a new generator instance together with the correct source code assignment
   *
   * @param generator the generator instance to add
   * @param sourceAssignment the source code type to distinguish between other generators
   */
  public addGenerator(generator: IGenerator, sourceAssignment: string): void {
    this._generatorMap[sourceAssignment.toUpperCase()] = generator;
  }

  /**
   * Retrieve a code generator for a given code type
   *
   * @param sourceAssignment the identifier (file ending) for which type of source code the generator is needed
   */
  public getGenerator(sourceAssignment: string = ""): IGenerator | undefined {
    return this._generatorMap[sourceAssignment.toUpperCase()];
  }
}

import { IGenerator} from '../Interfaces';
import { Node } from '../utils/Node';
import { FileType } from '../enums';

import { mkdirSync, writeFileSync } from 'fs';
import { join } from 'path';

/**
 * Implementation of the Generator interface to generate files and directories
 */
export class DirectoryGenerator implements IGenerator {
  /**
   * @override
   */
  public generate(oAST: Node, options?: { filePath: string }): string {
    const route = options?.filePath || '.';

    const aItems = oAST.children;
    if (aItems) {
      this._processFiles(aItems, route);
    }
    // Sry for this, but it is the only class not delivering a correct source code.
    return 'Done';
  }

  /**
   * Recursive file and folder generation
   *
   * @param aNodesToProcess the tree nodes to process
   * @param sTargetPath the path of the parent element
   */
  private _processFiles(aNodesToProcess: Node[], sTargetPath: string) {
    for (const oNode of aNodesToProcess) {
      if (oNode.type === FileType.Folder) {
        mkdirSync(join(sTargetPath, oNode.name));
        this._processFiles(oNode.children || [], join(sTargetPath, oNode.name));
      } else {
        if (oNode.content && oNode.content !== '') {
          writeFileSync(join(sTargetPath, oNode.name), oNode.content);
        }
      }
    }
  }
}

import { ParserFactory } from './Parser';
import { GeneratorFactory } from './Generator';
import { RuleSet } from './RuleSet';
import { Node } from './utils/Node';

export class Imposer {
  private parserFactory: ParserFactory;
  private generatorFactory: GeneratorFactory;
  private ruleSet: RuleSet;

  constructor(parser: ParserFactory, generator: GeneratorFactory, rules: RuleSet) {
    this.parserFactory = parser;
    this.generatorFactory = generator;
    this.ruleSet = rules;
  }

  public impose(baseFST: Node, featureFST: Node, visitorKeys: { [key: string]: string[] }): Node {
    const match = this.ruleSet.getMatchingRule(baseFST, featureFST);
    if (match) {
      return match.apply(baseFST, featureFST, this);
    }

    /* new strategy:
     * 1. check if rule is matching
     * 2. otherwise traverse deeper
     * 3. comparison by id and path
     */

    const baseKeys: string[] = Object.keys(baseFST).sort((a, b) => a.localeCompare(b));

    visitorKeys[baseFST.type].forEach((childKeys) => {
      const index: number = baseKeys.indexOf(childKeys);
      if (index > -1) {
        baseKeys.splice(index, 1);
      }
    });

    const newNode = baseFST.clone();
    //just add all necessary informations to the resulting node
    /* JS-Way ;)
      for (var key of aBaseKeys) {
       oNewNode[key] = baseFST[key];
      } 
    */

    newNode.featureName = featureFST.featureName;
    const childKeys = visitorKeys[baseFST.type];
    // check all childs and include all childs

    childKeys.forEach((childKey) => {
      const baseChilds = baseFST[childKey];
      const featureChilds = featureFST[childKey];
      let resultingChilds: Node[] = [];

      if (typeof baseChilds === 'object' && Array.isArray(baseChilds)) {
        baseChilds.forEach((childBase) => {
          childBase.featureName = baseFST.featureName;
          const childIndex: number[] = [];
          const childFeature = featureChilds.filter((featureChild: Node, index: number) => {
            if (featureChild.path === childBase.path && featureChild.name === childBase.name) {
              childIndex.push(index);
              return true;
            } else {
              return false;
            }
          });

          // if no child for the feature can be found take the base one
          if (childFeature.length === 0) {
            childBase.featureName = featureFST.featureName;
            resultingChilds.push(childBase);
          }

          // if a feature child is matching
          if (childFeature.length === 1) {
            const newChild: Node = childFeature[0];
            newChild.featureName = featureFST.featureName;
            const subImposeChildOne = this.impose(childBase, newChild, visitorKeys);
            if (Array.isArray(subImposeChildOne)) {
              resultingChilds = resultingChilds.concat(subImposeChildOne);
            } else {
              resultingChilds.push(subImposeChildOne);
            }

            //remove the feature childs afterwards
            childFeature.splice(childIndex[0], 1);
          }

          if (childFeature.length > 1) {
            console.warn(
              'more than one child is matching find a rule to merge this node and its children more precisly.',
            );
            console.group();
            console.warn('Path: ' + baseFST.path);
            console.warn('Name: ' + baseFST.name);
            console.warn('PropertyKey: ' + childKey);
            console.group();
            childFeature.forEach((featureChild: Node) => {
              console.warn(featureChild.path + "' '" + featureChild.name);
            });
            console.groupEnd();
            console.groupEnd();

            const firstFeatureChild: Node = childFeature[0];

            const oSubImposeChildMany = this.impose(childBase, firstFeatureChild, visitorKeys);
            /* aResultingChilds.push(this.impose(oChildB, oChildF, mVisitorKeys)); */
            if (Array.isArray(oSubImposeChildMany)) {
              resultingChilds = resultingChilds.concat(oSubImposeChildMany);
            } else {
              resultingChilds.push(oSubImposeChildMany);
            }

            //remove the feature childs afterwards
            featureChilds.splice(childIndex[0], 1);
          }
        });

        // add missing 'new' feature childs
        resultingChilds = resultingChilds.concat(featureChilds);
        newNode[childKey] = resultingChilds;
      } else {
        if (typeof baseChilds === typeof featureChilds && typeof baseChilds === 'undefined') {
          newNode[childKey] = resultingChilds;
        } else if (typeof baseChilds === typeof featureChilds && !Array.isArray(featureChilds)) {
          baseChilds.featureName = baseFST.featureName;
          featureChilds.featureName = featureFST.featureName;
          // Keep it without result distinguish
          newNode[childKey] = this.impose(baseChilds, featureChilds, visitorKeys);
        } else {
          throw new Error('Non array children differ');
        }
      }
    });

    return newNode;
  }

  //#region getter/setter
  public setParserFactory(parser: ParserFactory): void {
    this.parserFactory = parser;
  }

  public setGeneratorFactory(generator: GeneratorFactory): void {
    this.generatorFactory = generator;
  }

  public setRuleSet(rules: RuleSet): void {
    this.ruleSet = rules;
  }

  public getParserFactory(): ParserFactory {
    return this.parserFactory;
  }

  public getGeneratorFactory(): GeneratorFactory {
    return this.generatorFactory;
  }

  public getRuleSet(): RuleSet {
    return this.ruleSet;
  }
  //#endregion
}

import { IParser } from './Interfaces';
import { DirectoryParser } from './parsers';
import { FileType } from './enums';

/**
 * ParserFactory, the factory implementation for the set of Parsers
 * to deliver the correct parser implementation for each type of source code
 */
export class ParserFactory {
  private _parserMap: { [key: string]: IParser } = {};

  constructor() {
    this._loadDefaultParser();
  }

  /**
   * Used at the constructor to load the directory generator for files and folders
   */
  private _loadDefaultParser(): void {
    const dirParser = new DirectoryParser();
    this.addParser(dirParser, FileType.File);
    this.addParser(dirParser, FileType.Folder);
  }

  /**
   * Add a new parser instance together with the correct source code assignment
   *
   * @param parser the parser instance to add
   * @param sourceAssignment the source code type to distinguish between other generators
   */
  public addParser(parser: IParser, sourceAssignment: string): void {
    this._parserMap[sourceAssignment.toUpperCase()] = parser;
  }

  /**
   * Retrieve a code generator for a given code type
   *
   * @param sourceAssignment the identifier (file ending) for which type of source code the generator is needed
   */
  public getParser(sourceAssignment: string = ""): IParser | undefined {
    return this._parserMap[sourceAssignment.toUpperCase()];
  }
}

import { IParser } from '../Interfaces';
import { Node } from '../utils/Node';
import { FileType } from '../enums';

import { lstatSync, readdirSync, readFileSync } from 'fs';
import { join, extname, basename } from 'path';

export class DirectoryParser implements IParser {
  private static visitorKeys: { [key: string]: string[] } = {
    Folder: ['children'],
    File: [],
  };

  parse(sFilePath: string, options?: { parent?: Node }): Node {
    const route = sFilePath || '.';
    const stats = lstatSync(route);
    const oToken = new Node();

    oToken.name = basename(route);
    oToken.sourcePath = route;

    if (stats.isDirectory()) {
      oToken.type = FileType.Folder;
    } else {
      oToken.type = FileType.File;
    }

    if (options?.parent) {
      oToken.path = options?.parent?.path !== '' ? `${options.parent.path}.${oToken.type}` : oToken.type;
      oToken.parent = options?.parent;
    } else {
      oToken.path = '';
      oToken.parent = undefined;
    }

    if (stats.isDirectory()) {
      oToken.children = readdirSync(route).map((child: string) => {
        return this.parse(join(route, child), { parent: oToken });
      });
    } else {
      oToken.ending = extname(route).replace('.', '').toUpperCase();
      oToken.content = readFileSync(route, { encoding: 'utf-8' });
    }

    return oToken;
  }

  getVisitorKeys(): { [key: string]: string[] } {
    return DirectoryParser.visitorKeys;
  }
}

/**
 * @todo remove later on or take it to the core implementation
 */
export class Node {
  public type: string = '';
  public start: number | undefined = 0;
  public end: number = 0;

  /*intermediate definitions */
  public name: string = '';
  public path: string = '';
  public sourcePath: string = '';
  public parent: Node | undefined = undefined;
  public children?: Node[];
  public ending?: string;
  public content?: string;
  public featureName?: string;
  [key: string]: any;

  private attributes: { [key: string]: any } = {};

  constructor(pos?: number) {
    this.start = pos;
  }

  getAttribute(attName: string): any | undefined {
    return this.attributes[attName];
  }

  setAttribute(attName: string, value: number | string | boolean | number[] | boolean[] | string[]): void {
    this.attributes[attName] = value;
  }

  public clone(): Node {
    const clone = new Node();
    clone.type = this.type;
    clone.start = this.start;
    clone.end = this.end;

    clone.name = this.name;
    clone.path = this.path;
    clone.sourcePath = this.sourcePath;
    clone.parent = this.parent;
    clone.children = this.children;
    clone.ending = this.ending;
    clone.content = this.content;
    clone.featureName = this.featureName;
    Object.keys(this.attributes).forEach((key) => {
      clone.setAttribute(key, this.getAttribute(key));
    });
    return clone;
  }
}

import { IRule } from './Interfaces';
import { Node } from './utils/Node';
import { fileImpose } from './rules';
import { Rule } from './Rule';

export class RuleSet {
  private _languageLimit: string = '';
  private _rules: Rule[] = [];

  constructor(rules?: Rule[]) {
    if (rules && rules.length > 0) {
      this._rules = rules;
    } else {
      this._loadDefaultRules();
    }
  }

  private _loadDefaultRules(): void {
    const rulePatterns: IRule[] = [fileImpose];
    this._rules = rulePatterns.map((rp) => new Rule(rp));
  }

  public addRule(rule: IRule): void {
    this._rules.push(new Rule(rule));
  }

  public addMultipleRules(rules: IRule[]): void {
    this._rules = this._rules.concat(rules.map((rp) => new Rule(rp)));
  }

  public getRules(): Rule[] {
    return this._rules;
  }

  public copy(): RuleSet {
    return new RuleSet(this._rules);
  }

  public limitToLanguage(lang: string = ''): void {
    this._languageLimit = lang;
  }

  public getMatchingRule(baseFST: Node, featureFST: Node): Rule | undefined {
    let aUsageRules = this._rules;
    if (this._languageLimit !== '') {
      aUsageRules = aUsageRules.filter((rule) => rule.supportsLanguage(this._languageLimit));
    }

    const resultingRules = aUsageRules.filter((rule) => rule.isMatching(baseFST, featureFST));
    if (resultingRules.length > 1) {
      // tslint:disable-next-line: no-console
      console.warn(
        'More than one rule is matching! Taking the first one to proceed. Result can differ from expected Result.',
      );
    }

    return resultingRules[0];
  }
}

import { IRule } from '../Interfaces';
import { Node } from '../utils/Node';
import { FileType } from '../enums';
import { Imposer } from '../Imposer';

export const rule: IRule = {
  apply: (baseFST: Node, featureFST: Node, context: Imposer): Node => {
    // 1. first step, parse the different files to get thier AST representations
    const baseParser = context.getParserFactory().getParser(baseFST.ending);
    const featureParser = context.getParserFactory().getParser(featureFST.ending);

    if (!baseParser || !featureParser) {
      throw new Error('Base-FeatureTree-Parser and Feature-FeatureTree-Parser not correctly defined');
    }

    if (!baseFST.content || !featureFST.content) {
      throw new Error('BaseFeatureTree and FeatureFeatureTree does not contain source code');
    }

    const baseAST = context.getParserFactory().getParser(baseFST.ending)?.parse(baseFST.content || "", {});
    const featureAST = context.getParserFactory().getParser(featureFST.ending)?.parse(featureFST.content || "", {});

    if (!baseAST || !featureAST) {
      throw new Error('BaseAST and FeatureAST not available')
    }

    baseAST.featureName = baseFST.featureName;
    featureAST.featureName = featureFST.featureName;

    // 2. impose both structures
    const resultingAST = context.impose(
      baseAST,
      featureAST,
      baseParser.getVisitorKeys(),
    );

    // 3. recreate the node for further processing
    const returningNode = featureFST.clone();
    const featureGenerator = context.getGeneratorFactory().getGenerator(featureFST.ending);
    if (!featureGenerator) { throw new Error('No suitable Generator available for feature context'); }
    returningNode.content = featureGenerator.generate(resultingAST);
    return returningNode;
  },
  target: [FileType.File, FileType.Folder],
  selector: `${FileType.File}[ending]`,
};

import * as cssWhat from 'css-what';
import { IRule } from './Interfaces';
import { Node } from './utils/Node';
import { Imposer } from './Imposer';

declare type ExtendedSelector = cssWhat.Selector & { level: number, name?: string, action?: string, attributes?: ExtendedSelector[] };

declare type SelectorCheck = {
  pathRegex: RegExp,
  propertyChecks: { [key: number]: ExtendedSelector },
  attributes: [],
  name: ''
}

export class Rule {
  private _targetNodes: string[];
  private _selector: string;
  private _selectorFeature: string;
  private _baseChecks: SelectorCheck[];
  private _featureChecks: SelectorCheck[];
  private _resolveCallback: (baseFST: Node, featureFST: Node, context: Imposer) => Node;

  constructor(rule: IRule) {
    if (Array.isArray(rule.target)) {
      this._targetNodes = rule.target;
    } else if (typeof rule.target === 'string') {
      this._targetNodes = [rule.target];
    } else {
      this._targetNodes = [];
    }

    this._selector = rule.selector;
    this._selectorFeature = rule?.selectorFeature || rule.selector;
    this._resolveCallback = rule.apply.bind(this);
    this._baseChecks = this._setupSelector(this._selector);
    this._featureChecks = this._setupSelector(this._selectorFeature);
  }

  public supportsLanguage(lang: string): boolean {
    return this._targetNodes.find((tar: string) => tar.toLowerCase() === lang.toLowerCase()) !== undefined;
  }

  public isMatching(baseFST: Node, featureFST: Node): boolean {
    // check if the path contains the correct form for the css selector
    const result: boolean =
      this._checkNodeMatching(baseFST, this._baseChecks) && this._checkNodeMatching(featureFST, this._featureChecks);
    return result;
  }

  public apply(baseFST: Node, featureFST: Node, context: Imposer): Node {
    return this._resolveCallback(baseFST, featureFST, context);
  }

  private _setupSelector(selector: string): SelectorCheck[] {
    const cssList: cssWhat.Selector[][] = cssWhat.parse(selector, {
      lowerCaseTags: false,
      lowerCaseAttributeNames: false,
      xmlMode: false,
    });

    return cssList.map((cssPart: cssWhat.Selector[]) => this._createSelectorChecks(cssPart));
  }

  private _createSelectorChecks(cssPart: cssWhat.Selector[]): SelectorCheck {
    // #region selectorBuildExplanation

    // Test mit mehreren sollte zu Regex führen
    // File[ending]>Whatever Test,
    // File[ending=JS]>Whatever Test,
    // Resultat: File\.Whatever\.[\w.]*Text
    /*
     * Array(6):
        0: {type: "tag", name: "file"}
        1: {type: "attribute", name: "ending", action: "exists", value: "", ignoreCase: false},
           {type: "attribute", name: "ending", action: "equals", value: "JS", ignoreCase: false}
        2: {type: "child"}
        3: {type: "tag", name: "whatever"}
        4: {type: "descendant"}
        5: {type: "tag", name: "test"}
     */
    /*
    var temp = cssList.map((oToken) => {
        if (oToken.type === "tag") {
            return oToken.name;
        }
        if (oToken.type === "child") {
            return "\\.";
        }
        if (oToken.type === "descendant") {
            return "\\.[\\w.]*"
        }
    }).filter(oPart => oPart);
    //the last tag has to be at the end of the string.
    temp[temp.length - 1] = "(" + temp[temp.length - 1] + ")$"
    var pathRegExp = new RegExp(temp.join(''));
    var level = 0;
    var checks = {};
    cssList.map((oToken) => {
        if (oToken.type === "tag") {
            level++;
        }
        oToken.level = level;
        return oToken;
    }).forEach((oToken) => {
        if (oToken.type === "tag") {
            checks[oToken.level] = oToken;
        }
        if (oToken.type === "attribute") {
            var attributes = checks[oToken.level]['attributes'];
            if (!attributes) {
                attributes = [];
            }
            attributes.push(oToken);
            checks[oToken.level]['attributes'] = attributes;
        }
    });
    Object.keys(checks).forEach((sKey) => {
        if (!checks[sKey].attributes) {
            delete checks[sKey]
        }
    });
    return {
        pathRegex: pathRegExp,
        propertyChecks: checks
    }*/
    //#endregion
    const temp = cssPart
      .map((token: cssWhat.Selector) => {
        if (token.type === 'tag') {
          return token.name;
        }
        if (token.type === 'child') {
          return '\\.';
        }
        if (token.type === 'descendant') {
          return '\\.[\\w.]*';
        }
      })
      .filter((part) => part);

    temp[temp.length - 1] = `(${temp[temp.length - 1]})$`;
    const pathRegExp: RegExp = new RegExp(temp.join(''));
    let level: number = 0;
    const checks: { [key: number]: ExtendedSelector } = {};
    cssPart
      .map((token: cssWhat.Selector) => {
        if (token.type === 'tag') {
          level++;
        }
        const refactoredToken: ExtendedSelector = { ...token, level: level };
        return refactoredToken;
      })
      .forEach((token: ExtendedSelector) => {
        if (token.type === 'tag') {
          checks[token.level] = token;
        }
        if (token.type === 'attribute') {
          let attributes = checks[token.level].attributes;
          if (!attributes) {
            attributes = [];
          }
          attributes.push(token);
          checks[token.level].attributes = attributes;
        }
      });

    Object.keys(checks).forEach((key: string) => {
      if (!checks[parseInt(key, 10)].attributes) {
        delete checks[parseInt(key, 10)];
      }
    });

    return {
      pathRegex: pathRegExp,
      propertyChecks: checks,
      attributes: [],
      name: ''
    };
  }

  private _checkNodeMatching(node: Node, checks: SelectorCheck[]) {
    return checks.some((check) => this._singleNodeCheck(node, check));
  }

  private _singleNodeCheck(node: Node, check: SelectorCheck): boolean {
    if (node.path && check.pathRegex.exec(node.path) !== null) {
      // check if the properties or the properties of the parents match the css selector
      if (Object.keys(check.propertyChecks).length > 0) {
        // check bottom to top
        const checkKeys: string[] = Object.keys(check.propertyChecks).slice().reverse();
        let latestNode: Node = node;
        for (const index of checkKeys) {
          /* 0: {type: "tag", name: "file"} 
             1: {type: "attribute", name: "ending", action: "exists", value: "", ignoreCase: false} */
          const checkProp = check.propertyChecks[parseInt(index, 10)];
          // iterate upwards until we found the correct one or the root node
          while (latestNode.type !== check.name && latestNode.parent) {
            if (latestNode.parent) {
              latestNode = latestNode.parent;
            }
          }

          //Just to ensure they are not undefined ... (Hello TypeScript -_-)
          if (typeof checkProp.attributes === 'undefined') {
            checkProp.attributes = [];
          }

          for (const attributeCheck of checkProp.attributes) {
            const attCheck = attributeCheck as cssWhat.AttributeSelector;
            if (!attCheck.name) {
              continue;
            }
            const attName: string = attCheck.name;
            if (attCheck.action === 'exists' && typeof latestNode.getAttribute(attName) === 'undefined') {
              return false;
            }
            if (attCheck.action === 'equals' &&
              !(typeof latestNode.getAttribute(attName) !== 'undefined' && this._attributeFits(latestNode.getAttribute(attName), attCheck.value))) {
              return false;
            }
          }
        }
        // if everything matches we return with true;
        return true;
      } else {
        return true
      }
    } else {
      return false;
    }
  }

  private _attributeFits(nodeAtt: string, expectedValue: string): boolean {
    const objRegex: RegExp = /\{(\w+)=(\w+)\}/;
    const m = objRegex.exec(expectedValue);

    if (m !== null) {
      // The result can be accessed through the 'm'-variable.
      if (typeof nodeAtt === 'object' && !Array.isArray(nodeAtt)) {
        const attribute = nodeAtt[m[1]];
        if (typeof attribute === 'object') {
          return false;
        } else {
          return attribute === m[2];
        }
      } else {
        return false;
      }
    } else {
      return nodeAtt === expectedValue;
    }
  }
}
